import React, { useState } from 'react';
import axios from 'axios';
import './VcIssueModal.css';

// Ïö∞Î¶¨Í∞Ä ÎßåÎì† Î™©ÏóÖ VC Î∞úÍ∏â ÏÑúÎ≤Ñ Ï£ºÏÜå
const MOCK_ISSUER_API_ENDPOINT = 'http://localhost:8083/issue-vc';
// VCÎ•º Ï†ÄÏû•Ìï† Î©îÏù∏ Î∞±ÏóîÎìú Ï£ºÏÜå (ÎÇòÏ§ëÏóê ÏÇ¨Ïö©)
const SAVE_VC_API_ENDPOINT = 'http://localhost:8080/api/user/save-vc';


function VcIssueModal({ onClose }) {
    const [formData, setFormData] = useState({ name: '', id: '', did: '' });
    const [isLoading, setIsLoading] = useState(false);
    const [issuedVC, setIssuedVC] = useState(null);
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setError('');
        setIssuedVC(null);

        try {
            // 1. Î™©ÏóÖ VC Î∞úÍ∏â ÏÑúÎ≤ÑÏóê Ïù¥Î¶Ñ, ID, DIDÎ•º Î≥¥ÎÇ¥ VCÎ•º ÏöîÏ≤≠Ìï©ÎãàÎã§.
            const response = await axios.post(MOCK_ISSUER_API_ENDPOINT, formData);
            const receivedVC = response.data.vc;
            setIssuedVC(receivedVC);
            alert('VC Î∞úÍ∏â ÏÑ±Í≥µ! ÏïÑÎûò VCÎ•º Î≥µÏÇ¨ÌïòÏó¨ Î°úÍ∑∏Ïù∏ Ïãú ÏÇ¨Ïö©ÌïòÏÑ∏Ïöî.');

            // 2. (Ï∂îÍ∞Ä Í∏∞Îä•) Î∞úÍ∏âÎ∞õÏùÄ VCÎ•º Î©îÏù∏ Î∞±ÏóîÎìú DBÏóê Ï†ÄÏû•Ìï©ÎãàÎã§.
            const token = localStorage.getItem('jwt_token');
            if (token) {
                await axios.post(SAVE_VC_API_ENDPOINT,
                    { vc: JSON.stringify(receivedVC) },
                    { headers: { 'Authorization': `Bearer ${token}` } }
                );
            }

        } catch (err) {
            setError(`VC Î∞úÍ∏â Ïã§Ìå®: ${err.response?.data?.error || err.message}`);
        } finally {
            setIsLoading(false);
        }
    };

    const copyToClipboard = (text) => { /* ... Î≥µÏÇ¨ Î°úÏßÅ ... */ };

    return (
        <div className="vc-modal-overlay">
            <div className="vc-modal-content">
                <button className="vc-modal-close" onClick={onClose}>&times;</button>
                <h2>üìú ÏûêÍ≤©Ï¶ùÎ™Ö(VC) Î∞úÍ∏â</h2>
                {!issuedVC ? (
                    <form onSubmit={handleSubmit}>
                        <p>VCÎ•º Î∞úÍ∏âÎ∞õÍ∏∞ ÏúÑÌï¥ Ï†ïÎ≥¥Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.</p>
                        <input name="name" type="text" value={formData.name} onChange={handleChange} placeholder="Ïù¥Î¶Ñ" required />
                        <input name="id" type="text" value={formData.id} onChange={handleChange} placeholder="ÏïÑÏù¥Îîî" required />
                        <input name="did" type="text" value={formData.did} onChange={handleChange} placeholder="Î∞úÍ∏âÎ∞õÏùÄ DID" required />
                        <button type="submit" disabled={isLoading}>{isLoading ? 'Î∞úÍ∏â Ï§ë...' : 'VC Î∞úÍ∏â'}</button>
                        {error && <p style={{ color: 'red' }}>{error}</p>}
                    </form>
                ) : (
                    <div className="issued-info-display">
                        <h3>[Ï§ëÏöî] Î∞úÍ∏âÎêú VC Ï†ïÎ≥¥</h3>
                        <textarea value={JSON.stringify(issuedVC, null, 2)} readOnly rows="10" />
                        <button onClick={() => copyToClipboard(JSON.stringify(issuedVC))}>VC Î≥µÏÇ¨</button>
                    </div>
                )}
            </div>
        </div>
    );
}

export default VcIssueModal;
