import React, { useState } from 'react';
import axios from 'axios';
import './VcIssueModal.css';

const MOCK_ISSUER_API_ENDPOINT = 'http://localhost:8083/issue-vc';

function VcIssueModal({ onClose }) {
    const [formData, setFormData] = useState({ name: '', id: '', did: '' });
    const [isLoading, setIsLoading] = useState(false);
    const [issuedVC, setIssuedVC] = useState(null);
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setError('');
        setIssuedVC(null);

        try {
            const response = await axios.post(MOCK_ISSUER_API_ENDPOINT, formData);
            setIssuedVC(response.data.vc);
        } catch (err) {
            setError(`VC Î∞úÍ∏â Ïã§Ìå®: ${err.response?.data?.error || err.message}`);
        } finally {
            setIsLoading(false);
        }
    };

    // --- ‚ñº‚ñº‚ñº 1. ÌÅ¥Î¶ΩÎ≥¥Îìú Î≥µÏÇ¨ Í∏∞Îä• Íµ¨ÌòÑ ‚ñº‚ñº‚ñº ---
    const copyToClipboard = () => {
        if (!issuedVC) return;
        const vcString = JSON.stringify(issuedVC, null, 2);
        navigator.clipboard.writeText(vcString).then(() => {
            alert('‚úÖ VCÍ∞Ä ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!');
        }).catch(err => {
            console.error('ÌÅ¥Î¶ΩÎ≥¥Îìú Î≥µÏÇ¨ Ïã§Ìå®:', err);
            alert('‚ùå ÌÅ¥Î¶ΩÎ≥¥Îìú Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        });
    };

    // --- ‚ñº‚ñº‚ñº 2. ÌååÏùº Îã§Ïö¥Î°úÎìú Í∏∞Îä• Íµ¨ÌòÑ ‚ñº‚ñº‚ñº ---
    const downloadVCAsFile = () => {
        if (!issuedVC) return;
        const vcString = JSON.stringify(issuedVC, null, 2);
        const blob = new Blob([vcString], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = 'my-real-estate-vc.json'; // Îã§Ïö¥Î°úÎìúÎê† ÌååÏùº Ïù¥Î¶Ñ
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
    };

    return (
        <div className="vc-modal-overlay">
            <div className="vc-modal-content">
                <button className="vc-modal-close" onClick={onClose}>&times;</button>
                <h2>üìú ÏûêÍ≤©Ï¶ùÎ™Ö(VC) Î∞úÍ∏â</h2>

                {!issuedVC ? (
                    <form onSubmit={handleSubmit}>
                        <p>VCÎ•º Î∞úÍ∏âÎ∞õÍ∏∞ ÏúÑÌï¥ Ï†ïÎ≥¥Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.</p>
                        <input name="name" type="text" value={formData.name} onChange={handleChange} placeholder="Ïù¥Î¶Ñ" required />
                        <input name="id" type="text" value={formData.id} onChange={handleChange} placeholder="ÏïÑÏù¥Îîî" required />
                        <input name="did" type="text" value={formData.did} onChange={handleChange} placeholder="Î∞úÍ∏âÎ∞õÏùÄ DID" required />
                        <button type="submit" disabled={isLoading}>{isLoading ? 'Î∞úÍ∏â Ï§ë...' : 'VC Î∞úÍ∏â'}</button>
                        {error && <p style={{ color: 'red' }}>{error}</p>}
                    </form>
                ) : (
                    <div className="issued-info-display">
                        <h3>[Ï§ëÏöî] Î∞úÍ∏âÎêú VC Ï†ïÎ≥¥</h3>
                        <p>ÏïÑÎûò VC Ï†ïÎ≥¥Î•º Î≥µÏÇ¨ÌïòÍ±∞ÎÇò ÌååÏùºÎ°ú Îã§Ïö¥Î°úÎìúÌïòÏó¨ ÏïàÏ†ÑÌïòÍ≤å Î≥¥Í¥ÄÌïòÏÑ∏Ïöî. **Î°úÍ∑∏Ïù∏ Ïãú ÌïÑÏöîÌï©ÎãàÎã§.**</p>
                        <textarea value={JSON.stringify(issuedVC, null, 2)} readOnly rows="10" />

                        {/* --- ‚ñº‚ñº‚ñº Î≤ÑÌäº UI ÏàòÏ†ï ‚ñº‚ñº‚ñº --- */}
                        <div className="button-group">
                            <button onClick={copyToClipboard}>VC ÌÖçÏä§Ìä∏ Î≥µÏÇ¨</button>
                            <button onClick={downloadVCAsFile}>VC ÌååÏùºÎ°ú Îã§Ïö¥Î°úÎìú</button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default VcIssueModal;
