// src/components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';  // named export
import axios from 'axios';
import './Auth.css';

const AUTH_API = 'https://2094-165-229-229-106.ngrok-free.app/login';

export default function Login({ onLogin }) {
  const [form, setForm] = useState({ id: '', password: '' });
  const navigate = useNavigate();

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();

    // 1Ô∏è‚É£ ÎîîÎ≤ÑÍ∑∏: Î≥¥ÎÇ¥Í∏∞ ÏßÅÏ†Ñ payload
    console.log('üëâ Login payload:', {
      id: form.id,
      password: form.password
    });

    try {
      const res = await axios.post(
        AUTH_API,
        {
          id:       form.id,
          password: form.password
        },
        {
          headers: {
            'Content-Type': 'application/json',
            'ngrok-skip-browser-warning': 'true'
          }
        }
      );

      // 2Ô∏è‚É£ ÎîîÎ≤ÑÍ∑∏: ÏÑ±Í≥µ ÏùëÎãµ
      console.log('‚úÖ login success response:', res.data);

      const { message, token, role, user: userId } = res.data;
      console.log('message:', message);
      console.log('token:', token);
      console.log('role:', role, 'user:', userId);

      // 3Ô∏è‚É£ ÌÜ†ÌÅ∞ Ï†ÄÏû•
      localStorage.setItem('token', token);

      // 4Ô∏è‚É£ JWT ÎîîÏΩîÎî© (ÏÑ†ÌÉù)
      let email    = form.id;
      let username = userId;
      try {
        const decoded = jwtDecode(token);
        console.log('üîì decoded JWT:', decoded);
        email    = decoded.email    || email;
        username = decoded.username || username;
      } catch (err) {
        console.warn('‚ö†Ô∏è JWT decode failed:', err);
      }

      // 5Ô∏è‚É£ Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥ Ï†ÑÎã¨
      onLogin({ email, role, username });

      // 6Ô∏è‚É£ role Î∂ÑÍ∏∞
      if (role === 'agent') {
        navigate('/agent/mypage');
      } else {
        navigate('/user/mypage');
      }
    } catch (err) {
      // 7Ô∏è‚É£ ÎîîÎ≤ÑÍ∑∏: ÏóêÎü¨ ÏùëÎãµÍπåÏßÄ Î™®Îëê Î°úÍ∑∏
      console.error('‚ùå login error response.data:', err.response?.data);
      console.error('‚ùå login error status:', err.response?.status);
      alert(err.response?.data?.error || err.message || 'Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  return (
    <div className="auth-container">
      <form onSubmit={handleSubmit} className="auth-form">
        <input
          name="id"
          type="text"
          placeholder="ÏïÑÏù¥Îîî"
          value={form.id}
          onChange={handleChange}
          required
        />
        <input
          name="password"
          type="password"
          placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
          value={form.password}
          onChange={handleChange}
          required
        />
        <button type="submit">Î°úÍ∑∏Ïù∏</button>
      </form>
    </div>
  );
}
