// src/components/PropertyList.js
import React, {
  useEffect,
  useState,
  forwardRef,
  useImperativeHandle,
  useCallback
} from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
// import './PropertyList.css';

//const API_URL = 'https://2094-165-229-229-106.ngrok-free.app';
const API_URL = 'http://localhost:8080'; // Î°úÏª¨ Í∞úÎ∞úÏö©

// ÎÇ®ÏùÄ Ï¥à ‚Üí "H:MM:SS" Ìè¨Îß∑ Î≥ÄÌôò Ìï®Ïàò
function formatLeftTime(sec) {
  if (sec <= 0) return "0:00";
  const h = Math.floor(sec / 3600);
  const m = Math.floor((sec % 3600) / 60);
  const s = sec % 60;
  return h > 0
    ? `${h}:${String(m).padStart(2, "0")}:${String(s).padStart(2, "0")}`
    : `${m}:${String(s).padStart(2, "0")}`;
}

const PropertyList = forwardRef(({ user, mode = 'all', onReserve }, ref) => {
  const [properties, setProperties] = useState([]);
  const [now, setNow] = useState(Date.now()); // Ïã§ÏãúÍ∞Ñ Í∞±Ïã†Ïö©

  const navigate = useNavigate();

  // 1Ï¥àÎßàÎã§ ÌòÑÏû¨ ÏãúÍ∞Å Í∞±Ïã† (ÎÇ®ÏùÄ ÏãúÍ∞Ñ ÌëúÏãúÏö©)
  useEffect(() => {
    const timer = setInterval(() => setNow(Date.now()), 1000);
    return () => clearInterval(timer);
  }, []);

  const fetchProperties = useCallback(async () => {
    try {
      let url = mode === 'my'
        ? (user?.username
          ? `${API_URL}/my-properties?user=${user.username}`
          : '')
        : `${API_URL}/properties?user=admin`;

      if (!url) {
        setProperties([]);
        return;
      }

      const res = await axios.get(url, {
        headers: { 'ngrok-skip-browser-warning': 'true' }
      });
      const arr = Array.isArray(res.data.properties)
        ? res.data.properties
        : Array.isArray(res.data)
          ? res.data
          : [];
      setProperties(arr);
    } catch (err) {
      setProperties([]);
      console.error('‚ùå Îß§Î¨º Ï°∞Ìöå Ïã§Ìå®:', err.response?.data || err.message);
    }
  }, [user, mode]);

  useImperativeHandle(ref, () => ({ fetchProperties }));

  useEffect(() => {
    fetchProperties();
  }, [fetchProperties]);

  const handleReserve = async (property) => {
    if (!user?.username) {
      alert('Î°úÍ∑∏Ïù∏ ÌõÑ ÏòàÏïΩ Í∞ÄÎä•Ìï©ÎãàÎã§.');
      return;
    }
    // 12ÏãúÍ∞Ñ Îí§(Ï¥à Îã®ÏúÑ)
    const expiresAt = Math.floor(Date.now() / 1000) + 12 * 3600;
    const payload = {
      user: user.username,
      id: property.id,
      expiresAt
    };
    const token = localStorage.getItem('token');
    try {
      const res = await axios.post(
        `${API_URL}/reserve-property`,
        payload,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
            'ngrok-skip-browser-warning': 'true'
          }
        }
      );
      alert(res.data.message || '‚úÖ ÏòàÏïΩÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.');
      if (res.data.expiresAt) {
        const date = new Date(res.data.expiresAt * 1000);
        alert(
          '‚úÖ ÏòàÏïΩ Ïú†Ìö®Í∏∞Ìïú: ' +
          date.toLocaleString('ko-KR', { hour12: false })
        );
      }
      fetchProperties();
      onReserve?.();
    } catch (err) {
      console.error('‚ùå reserve-property error response:', err.response);
      const msg =
        err.response?.data?.error ||
        err.response?.data?.message ||
        err.message;
      alert('ÏòàÏïΩ Ïã§Ìå®: ' + msg);
    }
  };

  return (
    <div className="property-list">
      {properties.length === 0 ? (
        <p>
          {mode === 'my'
            ? 'üì≠ Îì±Î°ùÌïú Îß§Î¨ºÏù¥ ÏóÜÏäµÎãàÎã§.'
            : 'üì≠ Îì±Î°ùÎêú Îß§Î¨ºÏù¥ ÏóÜÏäµÎãàÎã§.'}
        </p>
      ) : (
        <div className="property-grid">
          {properties.map((p, i) => {
            // ÏòàÏïΩ ÎÇ®ÏùÄ ÏãúÍ∞Ñ Í≥ÑÏÇ∞
            let leftSeconds = null;
            if (p.reservedBy && p.expiresAt) {
              leftSeconds = p.expiresAt - Math.floor(now / 1000);
            }

            return (
              <div key={p.id || i} className="property-card">
                {/* --- ÏÇ¨ÏßÑ ÎØ∏Î¶¨Î≥¥Í∏∞ --- */}
                {p.photoUrl ? (
                  <div style={{
                    width: "100%",
                    height: 160,
                    marginBottom: 8,
                    overflow: "hidden",
                    borderRadius: 8,
                    background: "#eee",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center"
                  }}>
                    <img
                      src={p.photoUrl}
                      alt="Îß§Î¨ºÏÇ¨ÏßÑ"
                      style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        display: "block"
                      }}
                      onError={e => { e.target.style.display = "none"; }}
                    />
                  </div>
                ) : (
                  <div style={{
                    width: "100%",
                    height: 160,
                    marginBottom: 8,
                    background: "#eee",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    color: "#bbb"
                  }}>
                    <span>ÏÇ¨ÏßÑ ÏóÜÏùå</span>
                  </div>
                )}

                <h4>{p.address}</h4>
                <p>üë§ ÏÜåÏú†Ïûê: {p.ownerHistory?.slice(-1)[0]?.owner || '-'}</p>
                <p>
                  üí∞ Í∞ÄÍ≤©:{' '}
                  {p.priceHistory?.slice(-1)[0]?.price?.toLocaleString() || '-'}Ïõê
                </p>
                <div className="property-actions" style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
                  <button onClick={() => navigate(`/properties/${p.id}`)}>
                    ÏÉÅÏÑ∏Î≥¥Í∏∞
                  </button>
                  {p.reservedBy && leftSeconds > 0 ? (
                    <>
                      <span style={{ color: 'red', lineHeight: '32px' }}>ÏòàÏïΩÎê®</span>
                      <span style={{ color: '#555', marginLeft: 8 }}>
                        ÎÇ®ÏùÄ ÏãúÍ∞Ñ: {formatLeftTime(leftSeconds)}
                      </span>
                    </>
                  ) : (
                    <button onClick={() => handleReserve(p)}>
                      ÏòàÏïΩÌïòÍ∏∞
                    </button>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
});

export default PropertyList;
