// src/components/PropertyDetail.js
import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import './PropertyDetail.css';

// (1) Ï†ïÏ†Å ÌååÏùº ÎùºÏö∞ÌÑ∞ Í∏∞Î≥∏ URL
//     Ïã§Ï†ú ÏÑúÎ≤Ñ Ï£ºÏÜå/Ìè¨Ìä∏ÎÇò ngrok Ï£ºÏÜåÎ°ú Î≥ÄÍ≤ΩÌï¥Ï£ºÏÑ∏Ïöî.
//const API_URL = 'https://2094-165-229-229-106.ngrok-free.app';
const API_URL = 'http://localhost:8080'; // Î°úÏª¨ Í∞úÎ∞úÏö©

/**
 * UTC Î¨∏ÏûêÏó¥ÏùÑ KST(ÌïúÍµ≠ ÏãúÍ∞Ñ) ÌòïÏãùÏúºÎ°ú Î∞îÍøîÏ§çÎãàÎã§.
 * Î∞±ÏóîÎìúÍ∞Ä "YYYY-MM-DD HH:mm:ss" ÌòïÏãùÏúºÎ°ú ÎÇ¥Î†§Ï§ÄÎã§Í≥† Í∞ÄÏ†ï.
 */
function toKST(utcStr) {
  if (!utcStr) return '-';
  // "YYYY-MM-DD HH:mm:ss" ‚Üí "YYYY-MM-DDTHH:mm:ssZ" ÌòïÌÉúÎ°ú Î≥ÄÌôò (UTCÎ°ú Í∞ÑÏ£º)
  const iso = utcStr.replace(' ', 'T') + 'Z';
  const d = new Date(iso);

  return d.toLocaleString('ko-KR', {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: false,
    timeZone: 'Asia/Seoul'
  });
}

export default function PropertyDetail() {
  const { id } = useParams();
  const [property, setProperty] = useState(null);
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // 1) Îß§Î¨º Îã®Í±¥ Ï°∞Ìöå (admin Í≥†Ï†ï)
    axios
      .get(`${API_URL}/property/${id}?user=admin`, {
        headers: { 'ngrok-skip-browser-warning': 'true' }
      })
      .then(res => {
        let p = res.data.property;
        if (typeof p === 'string') {
          try { p = JSON.parse(p); } catch { }
        }
        console.log('üõ∞Ô∏è ÏÑúÎ≤ÑÏóêÏÑú ÎÇ¥Î†§Ï§Ä property ‚Üí', p);
        setProperty(p);
      })
      .catch(err => {
        console.error('‚ùå ÏÉÅÏÑ∏ Ï°∞Ìöå Ïã§Ìå®:', err.response?.data || err.message);
        setProperty(null);
      });

    // 2) Ïù¥Î†• Ï°∞Ìöå
    axios
      .get(`${API_URL}/property/history?id=${encodeURIComponent(id)}&user=admin`, {
        headers: { 'ngrok-skip-browser-warning': 'true' }
      })
      .then(res => {
        let h = res.data.history || res.data;
        if (typeof h === 'string') {
          try { h = JSON.parse(h); } catch { }
        }
        console.log('üõ∞Ô∏è ÏÑúÎ≤ÑÏóêÏÑú ÎÇ¥Î†§Ï§Ä history ‚Üí', h);
        setHistory(h);
      })
      .catch(err => {
        console.error('‚ùå Ïù¥Î†• Ï°∞Ìöå Ïã§Ìå®:', err.response?.data || err.message);
        setHistory([]);
      })
      .finally(() => {
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p>Î°úÎî© Ï§ë‚Ä¶</p>;
  if (!property) return <p>Ìï¥Îãπ Îß§Î¨ºÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.</p>;

  // ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî
  // 3) Ïù¥ÎØ∏ÏßÄ URL Í≤∞Ï†ï
  const rawImg = property.photoUrl || '';
  let imgSrc = null;

  if (rawImg) {
    if (rawImg.startsWith('https://2094-165-229-229-106.ngrok-free.app') || rawImg.startsWith('https://2094-165-229-229-106.ngrok-free.app')) {
      imgSrc = rawImg.replace('https://2094-165-229-229-106.ngrok-free.app', API_URL).replace('https://2094-165-229-229-106.ngrok-free.app', API_URL);
    } else if (rawImg.startsWith(API_URL)) {
      imgSrc = rawImg;
    } else if (rawImg.startsWith('http://') || rawImg.startsWith('https://')) {
      imgSrc = rawImg;
    } else {
      let normalized = rawImg.replace(/\\/g, '/');
      if (!normalized.startsWith('/')) normalized = '/' + normalized;
      imgSrc = `${API_URL}${normalized}`;
    }
  }
  console.log('üñºÔ∏è Ïã§Ï†úÎ°ú ÏÇ¨Ïö©ÌïòÎäî imgSrc ‚Üí', imgSrc);
  // ‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî‚Äî

  // 4) Í∞ÄÍ≤© / ÏÜåÏú†Ïûê / Îì±Î°ùÏùº(UTC‚ÜíKST) / ÏÉÅÌÉú
  const lastPriceEntry = property.priceHistory?.slice(-1)[0] || {};
  const priceNum = lastPriceEntry.price
    ? Number(lastPriceEntry.price).toLocaleString()
    : '-';

  const regUTC = lastPriceEntry.date || lastPriceEntry.timestamp || '';
  const regDateKST = regUTC ? toKST(regUTC) : '-';

  return (
    <div
      className="property-detail-card"
      style={{
        maxWidth: 800,
        margin: '2rem auto',
        padding: '1.5rem',
        background: '#fff',
        borderRadius: 8,
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
      }}
    >
      <Link
        to="/map"
        style={{
          textDecoration: 'none',
          color: '#555',
          marginBottom: '1rem',
          display: 'inline-block'
        }}
      >
        ‚Üê Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
      </Link>

      {/* ‚Äî Ïù¥ÎØ∏ÏßÄ ÏòÅÏó≠ ‚Äî */}
      <div
        style={{
          width: '100%',
          height: 0,
          paddingBottom: '56.25%', // 16:9 ÎπÑÏú®
          position: 'relative',
          marginBottom: '1rem',
