import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import PropertyList from './PropertyList';
const API_URL = 'https://2094-165-229-229-106.ngrok-free.app';

const UserMypage = ({ user }) => {
  const [myReservations, setMyReservations] = useState([]);

  // user?.usernameÏúºÎ°ú Íµ¨Ï°∞ ÌÜµÏùº!
  const fetchReservations = useCallback(async () => {
    if (!user?.username) return;
    const token = localStorage.getItem('token');
    try {
      const res = await axios.get(
        `${API_URL}/api/reservations?userId=${user.username}`,
        { headers: { 'Authorization': `Bearer ${token}` } }
      );
      setMyReservations(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error('ÎÇ¥ ÏòàÏïΩ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', err);
    }
  }, [user]);

  useEffect(() => {
    fetchReservations();
  }, [fetchReservations]);

  return (
    <div className="mypage-container">
      <h2>ÎßàÏù¥ÌéòÏù¥ÏßÄ</h2>
      <p><strong>ÏïÑÏù¥Îîî:</strong> {user?.username || user?.email || '-'}</p>
      <div className="mypage-card">
        <h2>üìå ÎÇ¥ ÏòàÏïΩ Î™©Î°ù</h2>
        {myReservations.length === 0 ? (
          <p>ÏòàÏïΩÌïú Îß§Î¨ºÏù¥ ÏóÜÏäµÎãàÎã§.</p>
        ) : (
          <ul>
            {(Array.isArray(myReservations) ? myReservations : []).map(resv => (
    <li key={resv.reservationId}>
      [Îß§Î¨ºID: {resv.propertyId}] {resv.date} {resv.time} - {resv.status}
    </li>
  ))}
          </ul>
        )}
      </div>

      {/* ÎÇ¥Í∞Ä Îì±Î°ùÌïú Îß§Î¨º Î¶¨Ïä§Ìä∏ÎèÑ Î≥¥Ïó¨Ï£ºÍ∏∞! */}
      <div className="mypage-card">
        <h2>üè† ÎÇ¥Í∞Ä Îì±Î°ùÌïú Îß§Î¨º Î™©Î°ù</h2>
        <PropertyList user={user} mode="my" />
      </div>
    </div>
  );
};

export default UserMypage;
