import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.css';

const Header = ({ user, onLogout }) => {
  const navigate = useNavigate();
  const [scrolled, setScrolled] = useState(false);

  // Ïä§ÌÅ¨Î°§ Í∞êÏßÄ
  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 50);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Î°úÍ∑∏ÏïÑÏõÉ
  const handleLogout = () => {
    localStorage.removeItem('token');
    onLogout();
    navigate('/login');
  };

  // ÎÇ¥ ÌéòÏù¥ÏßÄ Î≤ÑÌäº ÌÅ¥Î¶≠
  const handleMyPage = () => {
    console.log('üë§ Header user prop:', user);
    if (user?.role === 'agent') {
      console.log('üöÄ Header navigation to /agent/mypage');
      navigate('/agent/mypage');
    } else if (user?.role === 'user') {
      console.log('üöÄ Header navigation to /user/mypage');
      navigate('/user/mypage');
    } else {
      console.log('üöÄ Header navigation fallback to /mypage');
      navigate('/mypage');
    }
  };

  return (
    <header className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-logo">
  <img
    src="/truehome-logo.png"
    alt="TrueHome Logo"
    className="logo-img"
    onClick={() => navigate('/')}
    style={{ cursor: 'pointer' }}
  />
</div>


      <nav className="navbar-menu">
        <ul className="navbar-list">
          <li onClick={() => navigate('/')} style={{ cursor: 'pointer' }}>Ìôà</li>
          <li onClick={() => navigate('/map')} style={{ cursor: 'pointer' }}>Îß§Î¨º</li>

          {user ? (
            <>
              <li onClick={handleMyPage} className="user-email" style={{ cursor: 'pointer' }}>
                {user.email}Îãò
              </li>
              <li onClick={handleLogout} style={{ cursor: 'pointer' }}>Î°úÍ∑∏ÏïÑÏõÉ</li>
            </>
          ) : (
            <>
              <li onClick={() => navigate('/login')} style={{ cursor: 'pointer' }}>Î°úÍ∑∏Ïù∏</li>
              <li onClick={() => navigate('/signup')} style={{ cursor: 'pointer' }}>ÌöåÏõêÍ∞ÄÏûÖ</li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
